import React from 'react';
import {minutesToDuration, secondsToDuration} from "../utils/duration"

export default function SessionTitle({session, focusDuration, breakDuration}) {

  let sessionTime = null;

  if (session?.label === "Focusing") {
    sessionTime = minutesToDuration(focusDuration);
  } else if (session?.label ==="On Break") {
    sessionTime = minutesToDuration(breakDuration);
  };



  return (
    <div className="row mb-2">
      <div className="col">
        {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
        <h2 data-testid="session-title">
          {session?.label} for {sessionTime} minutes
        </h2>
        {/* TODO: Update message below correctly format the time remaining in the current session */}
        <p className="lead" data-testid="session-sub-title">
          {secondsToDuration(session?.timeRemaining)} remaining
        </p>
      </div>
    </div>
  )
}
